import { useEffect, useState } from "react";
//import "./Posts.css";

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [selectedPost, setSelectedPost] = useState(null);
  const [comments, setComments] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        var user = JSON.parse(localStorage.getItem("currentUser"));
        const response = await fetch(
          `https://jsonplaceholder.typicode.com/users/${user.id}/posts`
        );
        const data = await response.json();
        setPosts(data);
      } catch (error) {
        console.error(error);
      }
    }

    fetchData();
  }, []);

  useEffect(() => {
    async function fetchComments() {
      if (selectedPost !== null) {
        const response = await fetch(
          `https://jsonplaceholder.typicode.com/posts/${selectedPost.id}/comments`
        );
        const data = await response.json();
        setComments(data);
      }
    }

    fetchComments();
  }, [selectedPost]);

  const handlePostClick = (post) => {
    setSelectedPost(post);
  };

  return (
    <div className="posts-container">
      <h1 className="posts-header">Posts</h1>
      <div className="posts-list">
        {posts.map((post) => (
          <div
            key={post.id}
            className={`post-item ${
              selectedPost !== null && selectedPost.id === post.id
                ? "selected-post"
                : ""
            }`}
            onClick={() => handlePostClick(post)}
          >
            <h2 className="post-title">{post.title}</h2>
            <p className="post-body">{post.body}</p>
          </div>
        ))}
      </div>
      {selectedPost !== null && (
        <div className="comments-container">
          <h2 className="comments-header">Comments for {selectedPost.title}</h2>
          <div className="comments-list">
            {comments.map((comment) => (
              <div key={comment.id} className="comment-item">
                <h3 className="comment-name">{comment.name}</h3>
                <p className="comment-body">{comment.body}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Posts;
