import { useState } from "react";
import ReactDOM from "react-dom/client";
import "./Registration.css";
import { Link } from "react-router-dom";

const Registration = () => {
  const [user, setUser] = useState({
    id: "",
    username: "",
    password: ""
  });

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setUser((prevUser) => ({ ...prevUser, [name]: value }));
  };

  
  async function fetchData() {
    try {
      const data = await requestsGet(`/passwords?username=${inputs.username}`);

      console.log(data);
      let exist = false;
      if (data[0].password === inputs.password) {
        //for (let i of data) {
        // if (
        //   i["username"] === inputs.username &&
        //   i["password"] === inputs.password
        // )
        {
          const dataUser = await requestsGet(
            `/users?username=${inputs.username}`
          );
          console.log(dataUser);

          var json = JSON.stringify(dataUser[0]);
          localStorage.setItem("currentUser", json);
          exist = true;
          window.location.href = "/Users";
        }
      }
      if (exist === false) {
        alert("Username or password is incorrect");
      }
    } catch (error) {
      console.error(error);
    }
  }


  const handleSubmit = (event) => {
    debugger;
    event.preventDefault();
    console.log(user);
    // Perform registration logic or API request here
    // Reset the form fields
    setUser({
      id: "",
      username: "",
      password: ""
    });
    fetchData();
  };

  return (
    <div className="registration-container">
      <form onSubmit={handleSubmit}>
        <h1>User Registration</h1>
        <div className="form-group">
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            name="username"
            value={user.username || ""}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={user.password || ""}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Register</button>
        <div><Link to={`/Login`}>Login</Link></div>
      </form>
    </div>
  );
};

export default Registration;








