import { useEffect, useState } from "react";
import "./Todos.css";

const Select = () => {
  const [selectedValue, setSelectedValue] = useState("");

  const handleChange = (event) => {
    setSelectedValue(event.target.value);
  };

  return (
    <div>
      <label htmlFor="select">Choose an option:</label>
      <select id="select" value={selectedValue} onChange={handleChange}>
        <option value="">--Please choose an option--</option>
        <option value="serial">serial</option>
        <option value="execution">execution</option>
        <option value="alphabetical">alphabetical</option>
        <option value="random">random</option>
      </select>
      <p>You selected: {selectedValue}</p>
    </div>
  );
};

const Todos = () => {
  const [todos, setTodos] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        var user = JSON.parse(localStorage.getItem("currentUser"));
        const response = await fetch(
          `https://jsonplaceholder.typicode.com/users/${user.id}/todos`
        );
        const data = await response.json();
        setTodos(data);
      } catch (error) {
        console.error(error);
      }
    }

    fetchData();
  }, []);

  const handleToggleTodo = (id) => {
    const updatedTodos = todos.map((todo) => {
      if (todo.id === id) {
        return {
          ...todo,
          completed: !todo.completed,
        };
      }
      return todo;
    });
    setTodos(updatedTodos);
  };

  return (
    <div className="todos-container">
      <h1 className="todos-header">Todos</h1>
      <Select />
      <div className="todos-list">
        {todos.map((todo) => (
          <div key={todo.id} className="todo-item">
            <label className="todo-label">
              <span className="todo-id">{todo.id}</span>
              <span className="todo-id">- </span>
              <span className="todo-title">{todo.title}</span>
              <input
                type="checkbox"
                className="todo-checkbox"
                checked={todo.completed}
                onChange={() => handleToggleTodo(todo.id)}
              />
              <span className="todo-checkmark"></span>
            </label>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Todos;

