import "./Info.css";
import { useEffect, useState } from "react";
import {
  requestsGet,
  requestsPost,
  requestsPut,
  requestsDelete,
} from "../requestsToServer.js";

const Info = () => {
  const [user, setUser] = useState(null);
  const [editDiv, setEditDiv] = useState(null);
  const [inputs, setInputs] = useState({});

  useEffect(() => {
    const currentUser = JSON.parse(localStorage.getItem("currentUser"));
    setUser(currentUser);
  }, []);

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs((values) => ({ ...values, [name]: value }));
  };

  const UpdateUser = function(){
    setEditDiv(null);
    let newInUser = {
      id: user.id,
      name: "talyaupdate",
      username: "talya",
      email: "talya@karina.biz",
      phone: "024-648-3800",
      website: "talya",
      rank: "user",
      api_key: "zLCyhlxcVRCisJNX9hUt",
    };
    //requestsPut("/users", newInUser);
    setInputs({});
  }

  const EditeUser = function(){
    setEditDiv(<div>
      <div className="info-details">
        <input id="NameAdit" className="info-item" type="text"
            value={inputs.username || ""}  onChange={handleChange}/>
        {/* <input id="UsernameAdit" className="info-item" value="Username"
            value={inputs.username || "Username"}/> */}
        <input  id="EmailAdit" className="info-item" type="text"
            value={inputs.username || "Email"}  onChange={handleChange}/>
        <input id="PhoneAdit" className="info-item" type="text"
            value={inputs.username || "Phone"}  onChange={handleChange}/>
        <input id="WebsiteAdit" className="info-item" type="text"
            value={inputs.username || "Website"}  onChange={handleChange}/>
        <button onClick={UpdateUser}>Update</button>
      </div>
    </div>)
  }

  return (
    <div className="info-container">
      <h1 className="info-header">Info</h1>
      {user && (
        <div className="background">
          <div className="info-details">
            <h3 className="info-item">ID: {user.id}</h3>
            <h3 className="info-item">Name: {user.name}</h3>
            <h3 className="info-item">Username: {user.username}</h3>
            <h3 className="info-item">Email: {user.email}</h3>

            <br />
            <h3 className="info-item">Phone: {user.phone}</h3>
            <h3 className="info-item">Website: {user.website}</h3>
          </div>
        </div>
      )}
      <button onClick={EditeUser}>Edit</button>
      <div>
        {editDiv}
      </div>
    </div>
  );
};

export default Info;
