import { useState, useEffect } from "react";
import "./Posts.css";
import {
  requestsGet,
  requestsPost,
  requestsPut,
  requestsDelete,
} from "../requestsToServer.js";

const CommentForm = ({ onAddComment }) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [body, setBody] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();
    onAddComment({ name, email, body });
    setName("");
    setEmail("");
    setBody("");
  };

  return (
    <form className="comment-form" onSubmit={handleSubmit}>
      <h3>Add Comment</h3>
      <div className="form-group">
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="body">Body:</label>
        <textarea
          id="body"
          value={body}
          onChange={(e) => setBody(e.target.value)}
          required
        ></textarea>
      </div>
      <button type="submit">Add</button>
    </form>
  );
};

const Comment = ({ comment, onUpdateComment, onDeleteComment }) => {
  return (
    <div className="comment-item">
      <h4 className="comment-name">{comment.name}</h4>
      <div className="comment-email">{comment.email}</div>
      <div className="comment-body">{comment.body}</div>
      <button
        className="edit-comment-btn"
        onClick={() => onUpdateComment(comment.id)}
      >
        Edit
      </button>
      <button
        className="delete-comment-btn"
        onClick={() => onDeleteComment(comment.id)}
      >
        Delete
      </button>
    </div>
  );
};

const Post = ({ post, onPostSelect, onUpdatePost, onDeletePost }) => {
  const [showComments, setShowComments] = useState(false);
  const [comments, setComments] = useState([]);
  const [editing, setEditing] = useState(false);
  const [editedTitle, setEditedTitle] = useState(post.title);
  const [editedBody, setEditedBody] = useState(post.body);

  const handleToggleComments = async () => {
    if (showComments) {
      setShowComments(false);
    } else {
      try {
        const data = await requestsGet(`/posts/${post.id}/comments`);
        setComments(data);
        setShowComments(true);
      } catch (error) {
        console.error(error);
      }
    }
  };

  const handleEditPost = () => {
    setEditing(true);
  };

  const handleCancelEdit = () => {
    setEditing(false);
    setEditedTitle(post.title);
    setEditedBody(post.body);
  };

  const handleUpdatePost = async () => {
    try {
      const updatedPost = await requestsPut(`/posts/${post.id}`, {
        title: editedTitle,
        body: editedBody,
      });
      onUpdatePost(updatedPost);
      setEditing(false);
    } catch (error) {
      console.error(error);
    }
  };

  const handleDeletePost = async () => {
    if (window.confirm("Are you sure you want to delete this post?")) {
      try {
        await requestsDelete(`/posts/${post.id}`);
        onDeletePost(post.id);
      } catch (error) {
        console.error(error);
      }
    }
  };

  return (
    <div className="post-item">
      <h2
        className={`post-title ${onPostSelect === post.id ? "active" : ""}`}
        onClick={() => onPostSelect(post.id)}
      >
        {editing ? (
          <input
            type="text"
            value={editedTitle}
            onChange={(e) => setEditedTitle(e.target.value)}
          />
        ) : (
          post.title
        )}
      </h2>
      <div className="post-body">
        {editing ? (
          <textarea
            value={editedBody}
            onChange={(e) => setEditedBody(e.target.value)}
          />
        ) : (
          post.body
        )}
      </div>
      <div className="post-actions">
        {editing ? (
          <React.Fragment>
            <button onClick={handleUpdatePost}>Save</button>
            <button onClick={handleCancelEdit}>Cancel</button>
          </React.Fragment>
        ) : (
          <React.Fragment>
            <button onClick={handleEditPost}>Edit</button>
            <button onClick={handleDeletePost}>Delete</button>
          </React.Fragment>
        )}
      </div>
      <button className="comments-toggle" onClick={handleToggleComments}>
        {showComments ? "Hide Comments" : "Show Comments"}
      </button>
      {showComments && (
        <div className="comments-container">
          {comments.map((comment) => (
            <div key={comment.id} className="comment-item">
              <h4 className="comment-name">{comment.name}</h4>
              <div className="comment-email">{comment.email}</div>
              <div className="comment-body">{comment.body}</div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

const PostForm = ({ onAddPost }) => {
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();
    onAddPost({ title, body });
    setTitle("");
    setBody("");
  };

  return (
    <form className="post-form" onSubmit={handleSubmit}>
      <h3>Add Post</h3>
      <div className="form-group">
        <label htmlFor="title">Title:</label>
        <input
          type="text"
          id="title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="body">Body:</label>
        <textarea
          id="body"
          value={body}
          onChange={(e) => setBody(e.target.value)}
          required
        ></textarea>
      </div>
      <button type="submit">Add</button>
    </form>
  );
};

const Posts = () => {
  // ...

  const handleUpdatePost = (updatedPost) => {
    setPosts((prevPosts) =>
      prevPosts.map((post) => (post.id === updatedPost.id ? updatedPost : post))
    );
  };

  const handleDeletePost = (deletedPostId) => {
    setPosts((prevPosts) =>
      prevPosts.filter((post) => post.id !== deletedPostId)
    );
    setSelectedPost(null);
  };

  return (
    <div className="posts-container">
      {/* ... */}
      <div className="posts-list">
        {posts.map((post) => (
          <Post
            key={post.id}
            post={post}
            onPostSelect={handlePostSelect}
            onUpdatePost={handleUpdatePost}
            onDeletePost={handleDeletePost}
          />
        ))}
      </div>
    </div>
  );
};

export default Posts;
