import { useEffect, useState } from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  useParams,
} from "react-router-dom";

const Photos = () => {
  const [photos, setPhotos] = useState([]);
  const [loadedPhotos, setLoadedPhotos] = useState([]);
  const [displayCount, setDisplayCount] = useState(10);
  let { userId, albumId } = useParams();

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch(
          `https://jsonplaceholder.typicode.com/albums/${albumId}/photos`
        );
        const data = await response.json();
        setPhotos(data);
        setLoadedPhotos(data.slice(0, displayCount));
      } catch (error) {
        console.error(error);
      }
    }

    fetchData();
  }, [albumId]);

  const loadMorePhotos = () => {
    const newDisplayCount = displayCount + 10;
    setLoadedPhotos(photos.slice(0, newDisplayCount));
    setDisplayCount(newDisplayCount);
  };

  return (
    <div>
      <h1>Photos</h1>
      {loadedPhotos.map((photo) => (
        <img key={photo.id} src={photo.thumbnailUrl} alt={photo.title} />
      ))}
      {loadedPhotos.length < photos.length && (
        <button onClick={loadMorePhotos}>Load More</button>
      )}
    </div>
  );
};

export default Photos;
