import { useEffect, useState } from "react";
import "./Posts.css";
import {
  requestsGet,
  requestsPost,
  requestsPut,
  requestsDelete,
} from "../requestsToServer.js";

const Comment = ({ comment, onUpdateComment, onDeleteComment }) => {
  const [editing, setEditing] = useState(false);
  const [editedName, setEditedName] = useState(comment.name);
  const [editedEmail, setEditedEmail] = useState(comment.email);
  const [editedBody, setEditedBody] = useState(comment.body);

  const handleEditComment = () => {
    setEditing(true);
  };

  const handleCancelEdit = () => {
    setEditing(false);
    setEditedName(comment.name);
    setEditedEmail(comment.email);
    setEditedBody(comment.body);
  };

  const handleUpdateComment = async () => {
    try {
      const updatedComment = await requestsPut(`/comments/${comment.id}`, {
        name: editedName,
        email: editedEmail,
        body: editedBody,
      });
      onUpdateComment(updatedComment);
      setEditing(false);
    } catch (error) {
      console.error(error);
    }
  };

  const handleDeleteComment = async () => {
    if (window.confirm("Are you sure you want to delete this comment?")) {
      try {
        await requestsDelete(`/comments/${comment.id}`);
        onDeleteComment(comment.id);
      } catch (error) {
        console.error(error);
      }
    }
  };

  return (
    <div className="comment-item">
      {editing ? (
        <div className="comment-edit">
          <input
            type="text"
            value={editedName}
            onChange={(e) => setEditedName(e.target.value)}
          />
          <input
            type="text"
            value={editedEmail}
            onChange={(e) => setEditedEmail(e.target.value)}
          />
          <textarea
            value={editedBody}
            onChange={(e) => setEditedBody(e.target.value)}
          />
          <div className="comment-edit-actions">
            <button onClick={handleUpdateComment}>Save</button>
            <button onClick={handleCancelEdit}>Cancel</button>
          </div>
        </div>
      ) : (
        <div>
          <h4 className="comment-name">{comment.name}</h4>
          <div className="comment-email">{comment.email}</div>
          <div className="comment-body">{comment.body}</div>
          <div className="comment-actions">
            <button onClick={handleEditComment}>Edit</button>
            <button onClick={handleDeleteComment}>Delete</button>
          </div>
        </div>
      )}
    </div>
  );
};

const Post = ({ post, onPostSelect }) => {
  const [showComments, setShowComments] = useState(false);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState({
    name: "",
    email: "",
    body: "",
  });

  const handleToggleComments = async () => {
    if (showComments) {
      setShowComments(false);
    } else {
      try {
        const data = await requestsGet(`/posts/${post.id}/comments`);
        setComments(data);
        setShowComments(true);
      } catch (error) {
        console.error(error);
      }
    }
  };

  const handleAddComment = async () => {
    try {
      const createdComment = await requestsPost(`/posts/${post.id}/comments`, {
        name: newComment.name,
        email: newComment.email,
        body: newComment.body,
      });
      setComments([...comments, createdComment]);
      setNewComment({ name: "", email: "", body: "" });
    } catch (error) {
      console.error(error);
    }
  };

  const handleUpdateComment = (updatedComment) => {
    const updatedComments = comments.map((comment) => {
      if (comment.id === updatedComment.id) {
        return updatedComment;
      }
      return comment;
    });
    setComments(updatedComments);
  };

  const handleDeleteComment = (commentId) => {
    const updatedComments = comments.filter(
      (comment) => comment.id !== commentId
    );
    setComments(updatedComments);
  };

  return (
    <div className="post-item">
      <h2
        className={`post-title ${onPostSelect === post.id ? "active" : ""}`}
        onClick={() => onPostSelect(post.id)}
      >
        {post.title}
      </h2>
      <div className="post-body">{post.body}</div>
      <button className="comments-toggle" onClick={handleToggleComments}>
        {showComments ? "Hide Comments" : "Show Comments"}
      </button>
      {showComments && (
        <div className="comments-container">
          {comments.map((comment) => (
            <Comment
              key={comment.id}
              comment={comment}
              onUpdateComment={handleUpdateComment}
              onDeleteComment={handleDeleteComment}
            />
          ))}
          <div className="comment-form">
            <input
              type="text"
              placeholder="Name"
              value={newComment.name}
              onChange={(e) =>
                setNewComment({ ...newComment, name: e.target.value })
              }
            />
            <input
              type="email"
              placeholder="Email"
              value={newComment.email}
              onChange={(e) =>
                setNewComment({ ...newComment, email: e.target.value })
              }
            />
            <textarea
              placeholder="Comment"
              value={newComment.body}
              onChange={(e) =>
                setNewComment({ ...newComment, body: e.target.value })
              }
            />
            <button onClick={handleAddComment}>Add Comment</button>
          </div>
        </div>
      )}
    </div>
  );
};

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [selectedPost, setSelectedPost] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        var user = JSON.parse(localStorage.getItem("currentUser"));

        const data = await requestsGet(`/users/${user.id}/posts`);
        setPosts(data);
      } catch (error) {
        console.error(error);
      }
    }

    fetchData();
  }, []);

  const handlePostSelect = (postId) => {
    setSelectedPost(postId);
  };

  return (
    <div className="posts-container">
      <h1 className="posts-header">Posts</h1>
      <div className="posts-list">
        {posts.map((post) => (
          <Post key={post.id} post={post} onPostSelect={handlePostSelect} />
        ))}
      </div>
    </div>
  );
};

export default Posts;
