import { useEffect, useState } from "react";
import "./Posts.css";

const Album = ({ album, onAlbumSelect }) => {
  const [showComments, setShowComments] = useState(false);
  const [comments, setComments] = useState([]);

  const handleToggleComments = async () => {
    if (showComments) {
      setShowComments(false);
    } else {
      try {
        const response = await fetch(
          `https://jsonplaceholder.typicode.com/albums/${album.id}/photos`
        );
        const data = await response.json();
        setComments(data);
        setShowComments(true);
      } catch (error) {
        console.error(error);
      }
    }
  };

  return (
    <div className="post-item" onClick={() => onAlbumSelect(album.id)}>
      <h2
        className={`post-title ${onAlbumSelect === album.id ? "active" : ""}`}
      >
        {album.title}
      </h2>
      {/* <button className="comments-toggle" onClick={handleToggleComments}>
        {showComments ? "Hide Comments" : "Show Comments"}
      </button>
      {showComments && (
        <div className="comments-container">
          {comments.map((comment) => (
            <div key={comment.id} className="comment-item">
              <h4 className="comment-name">{comment.name}</h4>
              <div className="comment-email">{comment.email}</div>
              <div className="comment-body">{comment.body}</div>
            </div>
          ))}
        </div>
      )} */}
    </div>
  );
};

const Albums = () => {
  const [albums, setAlbums] = useState([]);
  const [selectedPost, setSelectedPost] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        var user = JSON.parse(localStorage.getItem("currentUser"));
        const response = await fetch(
          `https://jsonplaceholder.typicode.com/users/${user.id}/albums`
        );
        const data = await response.json();
        setAlbums(data);
      } catch (error) {
        console.error(error);
      }
    }

    fetchData();
  }, []);

  const handleAlbumSelect = (albumId) => {
    alert(albumId);
    //window.location.href = `Albums/${albumId}/Photos`;
    window.location.href = "/Photos";
  };

  return (
    <div className="posts-container">
      <h1 className="posts-header">Albums</h1>
      <div className="posts-list">
        {albums.map((album) => (
          <Album
            key={album.id}
            album={album}
            onAlbumSelect={handleAlbumSelect}
          />
        ))}
      </div>
    </div>
  );
};

export default Albums;
