import { useEffect, useState } from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  useParams,
} from "react-router-dom";
import "./Photos.css";

const Photos = () => {
  const [photos, setPhotos] = useState([]);
  const [loadedPhotos, setLoadedPhotos] = useState([]);
  const [displayCount, setDisplayCount] = useState(10);
  let { userId, albumId } = useParams();

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch(
          `https://jsonplaceholder.typicode.com/albums/${albumId}/photos`
        );
        const data = await response.json();
        setPhotos(data);
        setLoadedPhotos(data.slice(0, displayCount));
      } catch (error) {
        console.error(error);
      }
    }

    fetchData();
  }, [albumId]);

  const loadMorePhotos = () => {
    const newDisplayCount = displayCount + 10;
    setLoadedPhotos(photos.slice(0, newDisplayCount));
    setDisplayCount(newDisplayCount);
  };

  return (
    <div className="photos-container">
      <h1>Photos</h1>
      <div className="photo-grid">
        {loadedPhotos.map((photo) => (
          <div className="photo-item" key={photo.id}>
            <img src={photo.thumbnailUrl} alt={photo.title} />
            <div className="photo-overlay">
              <span className="photo-title">{photo.title}</span>
            </div>
          </div>
        ))}
      </div>
      {loadedPhotos.length < photos.length && (
        <button className="load-more-btn" onClick={loadMorePhotos}>
          Load More
        </button>
      )}
    </div>
  );
};

export default Photos;
