import { Outlet, Link } from "react-router-dom";

const Layout = () => {
  var user = JSON.parse(localStorage.getItem("currentUser"));
  return (
    <>
      <h1>{user.name}</h1>
      <Link to="/Login">
        <button
          onClick={() => {
            localStorage.removeItem("currentUser");
          }}
        >
          Logout
        </button>
      </Link>
      <nav>
        <ul>
          <li>
            <Link to={`/Layout/${user.id}/Posts`}>Posts</Link>
          </li>
          <li>
            <Link to={`/Layout/${user.id}/Todos`}>Todos</Link>
          </li>
          <li>
            <Link to="/Layout/Albums">Albums</Link>
          </li>
          <li>
            <Link to={`/Layout/${user.id}/Info`}>Info</Link>
          </li>
        </ul>
      </nav>

      <Outlet />
    </>
  );
};

export default Layout;
